var onAppActivatedCallback  = function (){
  console.log("App Ticket Section in section Activated");	
  hideAllFields();
  client.data.get("ticket").then (
				function(data) {
					console.log(data);
					console.log(data.ticket.type);									
					Type = data.ticket.type;
					if(Type == 'Markt'){
						Category = data.ticket.custom_fields.o_kategorie;
						SubCategory = data.ticket.custom_fields.o_unterkategorie;						
						if(Category == '' || Category == undefined){
							Category = data.ticket.custom_fields.cf_marktkategorie;							
						}							
						if(SubCategory == '' || SubCategory == undefined){
							SubCategory = data.ticket.custom_fields.cf_marktunterkategorie;
						}
							SubSubCategory = data.ticket.custom_fields.cf_marktunterunterkategorie;
							if(Category != null &&  SubCategory == null && SubSubCategory == null){
								showTypCat(Type,Category);
							}
							if(Category != null &&  SubCategory != null && SubSubCategory == null){
								showTypCatSub(Type,Category,SubCategory);
							}	
							if(Category != null &&  SubCategory != null && SubSubCategory != null){
								showTypCatSubSub(Type,Category,SubCategory,SubSubCategory);
							}
							
						}
					if(Type == 'Webshop'){
						Category = data.ticket.custom_fields.cf_webshopkategorie;
						SubCategory = data.ticket.custom_fields.cf_webshopunterkategorie;
						SubSubCategory = data.ticket.custom_fields.cf_webshopdetailkategorie;
						
							if(Category != null &&  SubCategory == null && SubSubCategory == null){
								showTypCat(Type,Category);
							}
							if(Category != null &&  SubCategory != null && SubSubCategory == null){
								showTypCatSub(Type,Category,SubCategory);
							}	
							if(Category != null &&  SubCategory != null && SubSubCategory != null){
								showTypCatSubSub(Type,Category,SubCategory,SubSubCategory);
							}
					}
					if(Type == 'Belieferung'){
						Category = data.ticket.custom_fields.cf_belieferungskategorie;
						SubCategory = data.ticket.custom_fields.cf_belieferungsunterkategorie;						
						SubSubCategory = data.ticket.custom_fields.cf_belieferungunterkategorieunterrubrik;
						if(Category != null && SubCategory == null){
								showTypCat(Type,Category);							
						}
						if(Category != null && SubCategory != null){
								showTypCatSub(Type,Category,SubCategory);							
						}
						if(Category != null &&  SubCategory != null && SubSubCategory != null){
								showTypCatSubSub(Type,Category,SubCategory,SubSubCategory);
							}
					}
					if(Type == 'App'){
						Category = data.ticket.custom_fields.cf_appkategorie;
						SubCategory = data.ticket.custom_fields.cf_appunterkatergorie;						
						
						if(Category != null && SubCategory == null){
								showTypCat(Type,Category);							
						}
						if(Category != null && SubCategory != null){
								showTypCatSub(Type,Category,SubCategory);							
						}
					}
				 
				}
				);
};

$(document).ready( function() {
    app.initialized()
        .then(function(_client){
				window.client = _client; 
				client.events.on("ticket.typeChanged", typeChangeCallback);
				client.events.on("ticket.customFieldChanged", customFieldChangeCallback);
				client.events.on("app.activated", onAppActivatedCallback);				
			}
		);
});









